ao.IRepository;import com.mor.blogengine.exception.ElementExistingException;import com.mor.blogengine.exception.IncorrectPropertyValueException;import com.mor.blogengine.exception.MissingPropertyException;import com.mor.blogengine.exception.NoMatchesFoundException;import com.mor.blogengine.model.BlogEntry;import com.mor.blogengine.xpath.SearchCriteria;import org.dom4j.DocumentException;import org.dom4j.tree.DefaultElement;import java.util.List;import java.util.Map;import java.util.Properties;import java.util.logging.Level;import java.util.logging.Logger;/** * * @author laurent */public class EntryController extends BlogControllerBase implements IBlogElementController<BlogEntry, DocumentException> {    private final IRepository<BlogEntry, DefaultElement, SearchCriteria, DocumentException> repo;    /**     * Construct Entry controller class using given properties configuration     * @param config  related entryControlle configuration     */    public EntryController(Properties config) throws MissingPropertyException, IncorrectPropertyValueException {        super(config);        repo = new BlogEntryRepository(mConfig, getDocument());    }    @Override    public Map<String, BlogEntry> getAllElements() {        try {            List<DefaultElement> elements = repo.getElementsForCriteria(SearchCriteria.ALL, null);            return getFactory().createEntryMap(elements);        } catch (NoMatchesFoundException ex) {            try {                trace(ex.getMessage());            } catch (MissingPropertyException | IncorrectPropertyValueException ex1) {                Logger.getLogger(EntryController.class.getName()).log(Level.SEVERE, null, ex1);            }        }        return null;    }    @Override    public boolean addNewElement(BlogEntry e) throws DocumentException {        try {            return repo.add(e);        } catch (ElementExistingException ex) {            Logger.getLogger(EntryController.class.getName()).log(Level.SEVERE, null, ex);        }        return false;    }    @Override    public boolean deleteElement(BlogEntry e) throws DocumentException {        try {            return repo.remove(e);        } catch (NoMatchesFoundException ex) {            Logger.getLogger(EntryController.class.getName()).log(Level.SEVERE, null, ex);        }        return false;    }    @Override    public boolean editElement(BlogEntry what, BlogEntry with) throws DocumentException {        try {            return repo.edit(what, with);        } catch (NoMatchesFoundException | ElementExistingException ex) {            return false;        }    }    @Override    public Map<String, BlogEntry> getElementsForDate(String d) {        try {            return getFactory().createEntryMap(repo.getElementsForCriteria(SearchCriteria.DATE, d));        } catch (NoMatchesFoundException ex) {            Logger.getLogger(EntryController.class.getName()).log(Level.SEVERE, null, ex);        }        return null;    }    /**     * For the moment there is no use of definition as no needs were shown To     * introduce parent entry concept     *     * @param parentID the parent node ID to check for     */    @Override    public Map<String, BlogEntry> getAllElements(String parentID) {        return getAllElements();    }}//~ Formatted by Jindent --- http://www.jindent.com