ngine.exception.IncorrectPropertyValueException;import com.mor.blogengine.exception.MissingPropertyException;import com.mor.blogengine.exception.NoMatchesFoundException;import com.mor.blogengine.model.BlogComment;import com.mor.blogengine.xpath.SearchCriteria;import org.dom4j.Document;import org.dom4j.DocumentException;import org.dom4j.tree.DefaultElement;import javax.naming.ConfigurationException;import java.util.List;import java.util.Properties;import java.util.logging.Level;import java.util.logging.Logger;/** * * @author laurent */public class BlogCommentRepository extends BlogRepositoryBase        implements IRepository<BlogComment, DefaultElement, SearchCriteria, DocumentException> {    /**     * Default constructor     *     * @param document document instance that holds blog data     * @param config global configuration file for application     */    public BlogCommentRepository(final Properties config, final Document document) throws ConfigurationException {        super(document, config);    }    /**     * add a comment to blog     *     * @param t the comment to add     * @return true if comment added correctly     * @throws ElementExistingException if element to add exist     * use {@link #append(BlogComment what, String parentID)}     */    @Override    public boolean add(BlogComment t) throws ElementExistingException {        return false;    }    /**     * append a comment to a parernt entry to a blog     *     * @param what the comment to append     * @param parentID the Id of parent entry to append to     * @return true if Category appended correctly     *     */    @Override    public boolean append(BlogComment what, String parentID) throws NoMatchesFoundException, ConfigurationException {        try {            trace("Appending element... " + what.getCommentText());        } catch (MissingPropertyException | IncorrectPropertyValueException ex) {            Logger.getLogger(BlogCommentRepository.class.getName()).log(Level.SEVERE, null, ex);        }        List<DefaultElement> foundList = searchEngine.getElementsForCriteria("Entry",                SearchCriteria.SINGLE, parentID);        try {            DefaultElement relatedEntry = foundList.get(0);            return handler.append(relatedEntry, what.toElement());        } catch (Exception ex) {            try {                trace(ex.getMessage());            } catch (MissingPropertyException | IncorrectPropertyValueException ex1) {                Logger.getLogger(BlogCommentRepository.class.getName()).log(Level.SEVERE, null, ex1);            }        }        throw new NoMatchesFoundException();    }    /**     * remove a comment to blog     *     * @param t the comment to remove     * @return true if Comment removed correctly     *     */    @Override    public boolean remove(BlogComment t) throws NoMatchesFoundException, DocumentException {        List<DefaultElement> list = getElementsForCriteria(SearchCriteria.SINGLE, t.getEntityID());        return handler.remove(list.get(0), t.getEntryID());    }    /**     * edit a comment in a blog     *     * @param t the comment to edit     * @param t2 the new comment     * @return true if Comment edited correctly     */    @Override    public boolean edit(BlogComment t, BlogComment t2) throws NoMatchesFoundException, DocumentException, ConfigurationException {        boolean removed = remove(t);        boolean appended = append(t2, t2.getEntryID());        return removed && appended;    }    /**     * Enable the search for comment and criteria in XML <br/>     *     * @param searchParam what to search<br/>     * @param paramValue search for what criteria<br/>     *     * @return list of results<br/>     */    @Override    public List<DefaultElement> getElementsForCriteria(SearchCriteria searchParam, String paramValue)            throws NoMatchesFoundException {        List<DefaultElement> list = searchEngine.getElementsForCriteria("Comment",                searchParam, paramValue);        if (list == null) {            throw new NoMatchesFoundException();        }        return list;    }    @Override    public boolean append(BlogComment what)            throws NoMatchesFoundException, ElementExistingException, DocumentException {        throw new UnsupportedOperationException("Not supported yet.");    }}//~ Formatted by Jindent --- http://www.jindent.com