assertEquals;import static org.junit.jupiter.api.Assertions.assertNull;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;/** * * @author laurent */public class TestBlogCategory extends PropertiesConsumingTestCase {    @Test    public void categorieSansParametres() {        BlogCategory bc = new BlogCategory();        assertNull(bc.getCatName());    }    @Test    public void categorieSansDescription() {        BlogCategory category = new BlogCategory("test sans description");        assertEquals("test sans description", category.toString());    }    @Test    public void categorieAvecDescription() {        BlogCategory category = new BlogCategory("Category",                "Description");        assertEquals("Name: Category Description: Description", category.toString());    }    @Test()    public void categorieSansNom() {        Exception e=assertThrows(IllegalArgumentException.class, () -> new BlogCategory(null, "test"));        Assertions.assertEquals("Name is required",e.getMessage());    }    @Test()    public void categorieAvecElementNull(){        Exception e=assertThrows(IllegalArgumentException.class, () -> {            DefaultElement de=null;            new BlogCategory(de);        });    }}